const e=JSON.parse('{"key":"v-1aa683b3","path":"/issue/%E5%8F%8C%E9%93%BE%E7%AC%94%E8%AE%B0%E4%B8%8Eflashcard.html","title":"对比双链笔记与Supermemo","lang":"zh-CN","frontmatter":{"lang":"zh-CN","title":"对比双链笔记与Supermemo","description":"双链笔记与supermemo等卡片记忆软件的对比","head":[["meta",{"property":"og:url","content":"https://oshibuki.github.io/issue/%E5%8F%8C%E9%93%BE%E7%AC%94%E8%AE%B0%E4%B8%8Eflashcard.html"}],["meta",{"property":"og:site_name","content":"Oshibuki的数字城堡"}],["meta",{"property":"og:title","content":"对比双链笔记与Supermemo"}],["meta",{"property":"og:description","content":"双链笔记与supermemo等卡片记忆软件的对比"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-05-06T11:56:24.000Z"}],["meta",{"property":"article:author","content":"Oshibuki"}],["meta",{"property":"article:modified_time","content":"2023-05-06T11:56:24.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"对比双链笔记与Supermemo\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2023-05-06T11:56:24.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"Oshibuki\\",\\"url\\":\\"https://oshibuki.github.io\\"}]}"]]},"headers":[],"git":{"createdTime":1683374184000,"updatedTime":1683374184000,"contributors":[{"name":"Oshibuki","email":"qerlkkjdfalfjelj12.adf2@gmail.com","commits":1}]},"readingTime":{"minutes":0.78,"words":235},"filePathRelative":"issue/双链笔记与flashcard.md","localizedDate":"2023年5月6日","excerpt":"<h1> 对比双链笔记与Supermemo</h1>\\n<p>双链笔记的优点是能够快速链接多个文档，但是，在什么情况下，才需要频繁查找这些四散的信息呢？</p>\\n<ul>\\n<li>信息不处于长期记忆，而是陌生、繁杂的短期记忆，因而无法轻松调用，必须查询文档</li>\\n<li>信息时效性随着时间流逝，离现在越近价值越高。</li>\\n</ul>\\n<p>也就是说，双链笔记是写给别人近期看的一次性文档。</p>\\n<p>相比之下，Supermemo是界面简朴，编辑困难，一切都围绕长期价值的个人记忆工具。因此 ，双链笔记偏向多人协作共享文档，Supermemo等闪卡软件则偏向于个人学习内化知识。</p>\\n<p>正所谓，无链不如双链，双链不如神经内链</p>"}');export{e as data};
